                t = tf.Tfidf_transform(input_feature="likeids",output_feature="tfidf")
                transformers = [("tfidf",t)]
                p = Pipeline(transformers)
                df2 = p.fit_transform(df)
                self.assertAlmostEqual(df2["tfidf"][0][u"1405744859651437"],0.174077655956)
